mapper.select:
SELECT *
FROM employee

	AND name = #{name}
	AND work_dept = #{work_dept}
	AND position = #{position}

mapper.update:
UPDATE employee
name = #{name, jdbcType=VARCHAR}, employee_no = #{employeeNo, jdbcType=VARCHAR}, work_dept = #{workDept, jdbcType=VARCHAR}, position = #{position, jdbcType=VARCHAR}, address = #{address, jdbcType=VARCHAR}, state = #{state, jdbcType=VARCHAR}, phone_no = #{phoneNo, jdbcType=VARCHAR}, sex = #{sex, jdbcType=VARCHAR}, area = #{area, jdbcType=VARCHAR}, job = #{job, jdbcType=VARCHAR}, email = #{email, jdbcType=VARCHAR}, picture = #{picture, jdbcType=VARCHAR}, home_no = #{homeNo, jdbcType=VARCHAR}, office_no = #{officeNo, jdbcType=VARCHAR}, hire_date = #{hireDate, jdbcType=TIMESTAMP},
WHERE idx = #{empIdx}
