mapper.update:
UPDATE employee
name = #{name_r, jdbcType=VARCHAR}, employee_no = #{employeeNo_r, jdbcType=VARCHAR}, work_dept = #{workDept_r, jdbcType=VARCHAR}, position = #{position_r, jdbcType=VARCHAR}, address = #{address_r, jdbcType=VARCHAR}, state = #{state_r, jdbcType=VARCHAR}, phone_no = #{phoneNo_r, jdbcType=VARCHAR}, sex = #{sex_r, jdbcType=VARCHAR}, area = #{area_r, jdbcType=VARCHAR}, job = #{job_r, jdbcType=VARCHAR}, email = #{email, jdbcType=VARCHAR}, picture = #{picture_r, jdbcType=VARCHAR}, home_no = #{homeNo_r, jdbcType=VARCHAR}, office_no = #{officeNo_r, jdbcType=VARCHAR},
WHERE employee_no = #{employeeNo_r}
